// Generated by CoffeeScript 1.6.3
(function() {
  $('#videos').fitVids();

  $('#new_video').on('submit', function(e) {
    var $form;
    e.preventDefault();
    $form = $(this);
    return $.ajax({
      type: 'POST',
      url: $form.attr('action'),
      data: $form.serialize(),
      success: function(video) {
        $form.get(0).reset();
        return $('#videos').prepend("<article>" + video.html + "</article>").fitVids();
      }
    });
  });

}).call(this);
